mapper.select:
SELECT
l.wind_code,
l.trade_date,
l.wind_name,
l.first_time,
l.limit_status,
l.listed_stock,
l.order_total,
l.volume_net_in,
l.price,
l.limit_up_x,
l.limit_up_n,
l.limit_up_m,
l.main_forces,
l.cost,
l.profit,
l.main_forces_in,
l.divergency,
l.main_forces_c_b,
l.main_forces_c_s,
l.main_forces_ntimes,
l.main_forces_stimes,
l.buy_avg_amount,
l.sell_avg_amount,
l.create_time,
l.update_time,
r.topic_id,
b.topic_name,
r.color,
r.stock_num,
r.topic_hot
FROM tb_limit_up_stock_info l
LEFT JOIN tb_limit_up_stock_topic_relation r ON l.wind_code = r.wind_code AND l.trade_date = r.trade_date
LEFT JOIN tb_base_topic b ON r.topic_id = b.topic_id


        l.wind_code = #{queryParam.windCode}


        AND l.trade_date  >=  #{queryParam.tradeDateStart}


        AND l.trade_date  <=  #{queryParam.tradeDateEnd}


        AND l.wind_name like CONCAT('%', #{queryParam.windName}, '%')


        AND l.first_time  >=  #{queryParam.firstTimeStart}


        AND l.first_time  <=  #{queryParam.firstTimeEnd}


        AND l.limit_status = #{queryParam.limitStatus}


        AND l.listed_stock  >=  #{queryParam.listedStockMin}


        AND l.listed_stock  <=  #{queryParam.listedStockMax}


        AND l.order_total  >=  #{queryParam.orderTotalMin}


        AND l.order_total  <=  #{queryParam.orderTotalMax}


        AND l.volume_net_in  >=  #{queryParam.volumeNetInMin}


        AND l.volume_net_in  <=  #{queryParam.volumeNetInMax}


        AND l.price  >=  #{queryParam.priceMin}


        AND l.price  <=  #{queryParam.priceMax}


        AND l.limit_up_x = #{queryParam.limitUpX}


        AND l.limit_up_n  >=  #{queryParam.limitUpNMin}


        AND l.limit_up_n  <=  #{queryParam.limitUpNMax}


        AND l.limit_up_m  >=  #{queryParam.limitUpMMin}


        AND l.limit_up_m  <=  #{queryParam.limitUpMMax}


        AND l.main_forces  >=  #{queryParam.mainForcesMin}


        AND l.main_forces  <=  #{queryParam.mainForcesMax}


        AND l.cost  >=  #{queryParam.costMin}


        AND l.cost  <=  #{queryParam.costMax}


        AND l.profit  >=  #{queryParam.profitMin}


        AND l.profit  <=  #{queryParam.profitMax}


        AND l.main_forces_in  >=  #{queryParam.mainForcesInMin}


        AND l.main_forces_in  <=  #{queryParam.mainForcesInMax}


        AND l.divergency  >=  #{queryParam.divergencyMin}


        AND l.divergency  <=  #{queryParam.divergencyMax}


        AND l.main_forces_c_b  >=  #{queryParam.mainForcesCBMin}


        AND l.main_forces_c_b  <=  #{queryParam.mainForcesCBMax}


        AND l.main_forces_c_s  >=  #{queryParam.mainForcesCSMin}


        AND l.main_forces_c_s  <=  #{queryParam.mainForcesCSMax}


        AND l.main_forces_ntimes  >=  #{queryParam.mainForcesNtimesMin}


        AND l.main_forces_ntimes  <=  #{queryParam.mainForcesNtimesMax}


        AND l.main_forces_stimes  >=  #{queryParam.mainForcesStimesMin}


        AND l.main_forces_stimes  <=  #{queryParam.mainForcesStimesMax}


        AND l.buy_avg_amount  >=  #{queryParam.buyAvgAmountMin}


        AND l.buy_avg_amount  <=  #{queryParam.buyAvgAmountMax}


        AND l.sell_avg_amount  >=  #{queryParam.sellAvgAmountMin}


        AND l.sell_avg_amount  <=  #{queryParam.sellAvgAmountMax}


        AND r.topic_id = #{queryParam.topicId}


        AND b.topic_name like CONCAT('%', #{queryParam.topicName}, '%')


        AND r.color = #{queryParam.color}


        AND r.stock_num  >=  #{queryParam.stockNumMin}


        AND r.stock_num  <=  #{queryParam.stockNumMax}


        AND r.topic_hot  >=  #{queryParam.topicHotMin}


        AND r.topic_hot  <=  #{queryParam.topicHotMax}


ORDER BY l.trade_date DESC, l.wind_code ASC

    LIMIT #{queryParam.pageSize} OFFSET #{queryParam.pageNo}

SELECT `wind_code`,`trade_date`
FROM `tb_limit_up_stock_info`
WHERE 1 = 1

    AND trade_date  >=  #{tradeDateStart}


    AND trade_date  <=  #{tradeDateEnd}
